mapper.insert:
insert into
    dish (name, category_id, price, image, description, status, create_time, update_time, create_user, update_user)
values (
        #{name},#{categoryId},#{price},#{image},#{description},#{status},#{createTime},#{updateTime},#{createUser},#{updateUser}
       )

mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_Time = #{updateTime}, update_User = #{updateUser},
where id = #{id}

mapper.select:
select d.*,c.name as categoryName from dish d left outer join category c on c.id = d.category_id


        and d.name like concat('%',#{name},'%')


        and d.category_id = #{categoryId}


        and d.status = #{status}


order by d.create_time desc

select * from dish


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}


order by create_time desc

select count(id) from dish


        and status = #{status}


        and category_id = #{categoryId}
